-- Criação do Banco de DadosCREATE DATABASE LojaBD;-- Tabela CategoriaCREATE TABLE Categorias (    Id INT PRIMARY KEY IDENTITY,    Nome NVARCHAR(100) NOT NULL);-- Tabela ProdutoCREATE TABLE Produtos (    Id INT PRIMARY KEY IDENTITY,    Nome NVARCHAR(100) NOT NULL,    Preco DECIMAL(18, 2) NOT NULL,    DataAdicionado DATETIME NOT NULL,    CategoriaId INT,    CONSTRAINT FK_Produto_Categoria FOREIGN KEY (CategoriaId) REFERENCES Categorias(Id));-- Tabela VendaCREATE TABLE Vendas (    Id INT PRIMARY KEY IDENTITY,    ProdutoId INT,    Quantidade INT NOT NULL,    DataVenda DATETIME NOT NULL,    CONSTRAINT FK_Venda_Produto FOREIGN KEY (ProdutoId) REFERENCES Produtos(Id));-- Inserir categoriasINSERT INTO Categorias (Nome) VALUES ('Eletrônicos');INSERT INTO Categorias (Nome) VALUES ('Roupas');-- Inserir produtosINSERT INTO Produtos (Nome, Preco, DataAdicionado, CategoriaId) VALUES ('TV', 1200.00, GETDATE(), 1);INSERT INTO Produtos (Nome, Preco, DataAdicionado, CategoriaId) VALUES ('Celular', 800.00, GETDATE(), 1);INSERT INTO Produtos (Nome, Preco, DataAdicionado, CategoriaId) VALUES ('Camisa', 50.00, GETDATE(), 2);-- Inserir vendasINSERT INTO Vendas (ProdutoId, Quantidade, DataVenda) VALUES (1, 3, GETDATE());INSERT INTO Vendas (ProdutoId, Quantidade, DataVenda) VALUES (2, 2, GETDATE());INSERT INTO Vendas (ProdutoId, Quantidade, DataVenda) VALUES (3, 5, GETDATE());select * from produtos;select * from sys.foreign_keysselect * from sys.key_constraints